package analyzer

import (
	"encoding/hex"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestItem(t *testing.T) {
	assert := assert.New(t)
	a, err := New()
	if !assert.NoError(err) {
		t.Fatal(err)
	}
	packet := &EQPacket{}
	packet.Data, err = hex.DecodeString("")
	if !assert.NoError(err) {
		t.Fatal(err)
	}
	item := a.ItemScan(packet)
	t.Log(item)

	t.Fatal("done")
}
